function main(p) {

    // p = string con parámetros en formato json. Transformar en objeto js.
    console.log(p);
    var param = JSON.parse(p);
        
    // Rest
    var cl = getDatosCliente(param.codigo_cliente);

    // 
    var total_cantidad = 0.0;
    var total = 0.0;
    var total_x_indice = 0.0;
    var registros = 0;
    var tabla = []; // vacía x defecto

    if ( cl.codigo_zona === "01") {
        // Excel
        tabla = leerExcel();
        tabla.forEach( function(reg) {
            total_x_indice += reg.cantidad * reg.precio * nt.indice;
            total_cantidad += reg.cantidad;
            total += reg.cantidad * reg.precio;        
            registros ++;
        })
    } else {
        // Txt
        tabla = leerArchivoTxt();
        tabla.forEach( function(reg) {
            total_x_indice += reg.cantidad * reg.precio * nt.indice;
            total_cantidad += reg.cantidad;
            total += reg.cantidad * reg.precio;        
            registros ++;
        })
    }

    // Resultado final en formato JSON

    var result = {
        codigo_cliente: cl.codigo_cliente,
        nombre_cliente: cl.nombre_cliente,
        codigo_medidor: param.codigo_medidor,
        codigo_zona: cl.codigo_zona,
        cantidad: total_cantidad,
        total: total,
        indice: total_x_indice,
        registros: registros
    }
    
    return JSON.stringify(result);

}

